
app.controller("myCtrl", function ($scope,$http) {
    $scope.subjects = [];
    $scope.adav=[];
    $http.get("js/Subjects.js").then(function (response) {
        $scope.subjects  = response.data;
        $scope.pageCount = Math.ceil($scope.subjects.length / 1);
    });


    $scope.prop = "name";
    $scope.sortBy = function (prop) {
        $scope.prop = prop;
    };

    $scope.begin = 0;
    $scope.pageCount = Math.ceil($scope.subjects.length / 1);
    $scope.first = function () {
        $scope.begin = 0;
    };
    $scope.prev = function () {
        if ($scope.begin > 0){
            $scope.begin-=1;
        }
    };
    $scope.next = function () {
        if ($scope.begin < ($scope.pageCount - 1) * 1){
            $scope.begin += 1;
        }
    };
    $scope.last = function () {
        $scope.begin = ($scope.pageCount - 1) * 1;
    };
});
